import React from "react";

const Msgbox = (props) => {
  const green = {
    borderRadius: "10px",
    backgroundColor: "#10ad3f",
    minHeight: "70px",
    margin: "10px auto",
    maxWidth: "70%",
    color: "white",
    padding:"20px",
    fontSize:"20px",
  };

  const grey = {
    borderRadius: "10px",
    backgroundColor: "#dae2e8",
    minHeight: "70px",
    margin: "10px auto",
    maxWidth: "70%",
    padding:"20px",
    fontSize:"20px",
    color: "#000",
  };
  

  return (
    <div>
      {props.sentbyme ? (
        <div class="container">
        <div class="message-blue">
            <p class="message-content">{props.msg.message}</p>
            <div class="message-timestamp-left">{props.msg.sent}</div>
        </div>
        </div>
      ) : (
        <div class="message-orange">
       <p class="message-content">{props.msg.message}</p>
            <div class="message-timestamp-left">{props.msg.sent}</div>
        </div>
      )}
    </div>
  );
};

export default Msgbox;
