import { io } from "socket.io-client";
import { useState, useEffect } from "react";
import Msgbox from "./components/msgbox";

const URL = "http://localhost:8000";

const socket = io(URL, {
  path: "/socket.io",
  autoConnect: false,
  reconnection: false,
});

function App() {
  const [sender, setSender] = useState("");
  const [receiver, setReceiver] = useState("");
  const [authtoken, setAuthToken] = useState("");
  const [message, setMessage] = useState("");
  const [connected, setConnected] = useState(false);
  const [chats, setChats] = useState([]);
  // console.log("socket: ", socket);

  useEffect(() => {
    socket.on("tosender", (data) => {
      console.log(data, socket.id);
    });
    return () => {
      socket.off("test");
    };
  }, []);

  useEffect(() => {
    socket.on("receive", (data) => {
      console.log(data);
      setChats((prev) => [...prev, data]);
    });

    return () => {
      socket.off("receive");
    };
  }, []);

  const handleSender = (e) => {
    e.preventDefault();
    socket.auth = { sender };
    socket.connect();
    setTimeout(() => {
      if (socket.connected) {
        console.log("socket.connected", socket);
        setConnected(true);
      }
    }, 300);
  };

  const handlePrivateMessages = (e) => {
    e.preventDefault();
    const sent = new Date();
    socket.emit("pmessaget_s", {
      message,
      sender,
      receiver,
      authtoken,
      sent,
    });
  };

  const boxstyle = {
    selfAlign: "center",
    padding: "10px",
    margin: "10px auto",
    maxWidth: "40%",
  };

  return (
    <div className="App">
      <h3 style={{ textAlign: "center" }}>Chat API Testing</h3>
      {connected ? (
        <div>
        <h4 style={{ marginLeft: "70px" }}>Your Username: {sender} </h4>
        <form onSubmit={handlePrivateMessages} action="submit">
        <div className="row">
          <input
            type="text"
            value={receiver}
            placeholder="Enter Receiver's username"
            onChange={(e) => setReceiver(e.target.value)}
            style={boxstyle}
          />
<label for="group">Group</label>

<select id="group">
  <option value="volvo">Volvo</option>
  <option value="saab">Saab</option>
  <option value="opel">Opel</option>
  <option value="audi">Audi</option>
</select>
          <input
            type="text"
            value={message}
            placeholder="Enter the message"
            onChange={(e) => setMessage(e.target.value)}
            style={boxstyle}
          />
          <br />
          <button
            className="btn btn-primary"
            type="submit"
            style={{ maxWidth: "50%", margin: "10px auto" }}
          >
            Send
          </button>
        </div>
      </form>

      <div>
        {chats.map((msg) => {
          return <Msgbox msg={msg} sentbyme={msg.sender === sender} key={Math.random()}/>;
        })}
      </div>
      </div>
      ) : (
        <form action="submit" onSubmit={handleSender} style={{}}>
          <div className="row">
            <input
              type="text"
              value={sender}
              placeholder="Enter Your(sender's) username"
              onChange={(e) => setSender(e.target.value)}
              style={boxstyle}
            />
            <input
              type="text"
              value={authtoken}
              placeholder="Enter Sender's auth token(passed as cookie)"
              onChange={(e) => setAuthToken(e.target.value)}
              style={boxstyle}
            />
            <button
              className="btn btn-primary"
              type="submit"
              style={{ maxWidth: "50%", margin: "10px auto" }}
            >
              Enter connection
            </button>
          </div>
        </form>
      )}

      
    </div>
  );
}

export default App;
