import React from "react";
import './msgbox.css';
const Msgbox = (props) => {

  return (
    <div>
       <div class="container">
      {props.sentbyme ? (
       
        <div class="message-blue">
          <p class="message-content">{props.msg.sender}</p>
            <p class="message-content">{props.msg.message}</p>
            <div class="message-timestamp-left">{new Date(props.msg.sent).getHours()}:{new Date(props.msg.sent).getMinutes()}</div>
        </div>
       
      ) : (
        <div class="message-orange">
           <h6>{props.msg.sender}</h6>
       <p class="message-content">{props.msg.message}</p>
            <div class="message-timestamp-left">{new Date(props.msg.sent).getHours()}:{new Date(props.msg.sent).getMinutes()}</div>
        </div>
      )}
    </div>
    </div>
  );
};

export default Msgbox;
