import React from "react";

const Msgbox = (props) => {
  const green = {
    borderRadius: "10px",
    backgroundColor: "#10ad3f",
    minHeight: "70px",
    margin: "10px auto",
    maxWidth: "70%",
    color: "white",
    padding:"20px",
    fontSize:"20px",
  };

  const grey = {
    borderRadius: "10px",
    backgroundColor: "#dae2e8",
    minHeight: "70px",
    margin: "10px auto",
    maxWidth: "70%",
    padding:"20px",
    fontSize:"20px",
    color: "#000",
  };
  

  return (
    <div>
      {props.sentbyme ? (
        <div style={green}>
          <div className="row">
            <div className="col">To: {props.msg.receiver}</div>
            <div className="col">Time: {props.msg.sent}</div>
          </div>
          <div className="row" style={{padding:"12px"}}>{props.msg.message}</div>
        </div>
      ) : (
        <div style={grey}>
          <div className="row">
            <div className="col">From: {props.msg.sender}</div>
            <div className="col">Time: {props.msg.sent}</div>
          </div>
          <div className="row" style={{padding:"12px"}}>{props.msg.message}</div>
        </div>
      )}
    </div>
  );
};

export default Msgbox;
